document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",(function(e){e.preventDefault();let t=this.getAttribute("href").substring(1);const c=document.getElementById(t).getBoundingClientRect().top-0;window.scrollBy({top:c,behavior:"smooth"})}))}),window.addEventListener("scroll",()=>{let e=window.scrollY;document.querySelectorAll(".nav-class").forEach((t,c)=>{t.offsetTop<=e&&(document.querySelectorAll("nav a").forEach(e=>{e.classList.contains("active")&&e.classList.remove("active")}),document.querySelectorAll(".main__slider-link")[c].classList.add("active"))})});const btn=document.querySelector(".header__btn");btn.addEventListener("click",(function(){document.querySelector(".header__menu").classList.toggle("active")}));